import Header from './Header';
import Footer from './Footer';
import { DISEASE_LIST_API } from '../constant/apiUrl';

class Layout extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            diseases:[],
            diseaseCat:{}
        }
    }

    componentDidMount(){
        this.loadDisease();
    }


    loadDisease =async()=>{
        let diseaseCat = {};
        await fetch(DISEASE_LIST_API)
        .then(response => response.json())
        .then((data) =>{
            data.results.forEach(function(disease){
                if(diseaseCat[disease.disease_type]){
                    diseaseCat[disease.disease_type].push(disease);
                }else{
                    diseaseCat[disease.disease_type] = [disease]
                }
            });
            this.setState({
                diseases: data.results,
                diseaseCat: diseaseCat
            })

           }

        )
    }


    render() {
        const {diseases} =this.state;
        console.log("hi", this.state)
        return (
            <section>
                <Header {...this.state}/>
                    {this.props.children}
                <Footer/>
            </section>
        );
    }
}


Layout.getInitialProps = async({ query }) => {

    const res = await fetch(DISEASE_LIST_API);

    const json = await res.json();
    return {
        contacts: json.results
    }
}
export default Layout;
