import React from 'react'
import Link from 'next/link'
import Layout from "../Components/Layout";
import { DISEASE_LIST_API } from '../constant/apiUrl';
import fetch from "isomorphic-unfetch";

class Index extends React.Component{
    static async getInitialProps() {
        await fetch(DISEASE_LIST_API)
        .then((res)=>{
            return res.json();
        })
        .then( (data) => {
            return {};
          console.log(data);
        });
    }
    constructor(props){
        super(props);
        this.state={

        }
    }

    render() {
        let {diseases} =this.props;
        return (
            <Layout title="Home">
                <div className="container-fluid top-space">

                    <Link href="/about">
                        <a>About Us</a>

                    </Link>
                    <h1>home</h1>
                    <ul>
                    {diseases.map(disease => (
                        <li>
                            {disease.domain}
                        </li>
                    ))}
                    </ul>
                </div>
            </Layout>
        );
    }
}

Index.getInitialProps = async function() {
    const res = await fetch('https://bk-erp.plutonic.co.in/erp-api/disease/');
    const data = await res.json();

    console.log('Show data:',data);

    return {
        diseases: data.results
    };
};


export default Index;
