import React from 'react';
import Layout from '../Components/Layout';
import { CONTACT_US } from '../constant/apiUrl';
// import fetch from "isomorphic-unfetch";

class Contact extends React.Component{
    constructor(props){
        super(props);
        this.state={

        }
    }

    componentDidMount() {
        fetch(CONTACT_US)
        .then(response => response.json())
        .then(data => this.setState({ data }));
      }




    render() {

        let {contacts} =this.props;
        console.log("prop",this.state)
        return (
            <Layout title="Contact Us">
                <div className="container top-space">
                    <div className="col-md-6">
                        <div class="headline">
                            <h2 className="headTitle"><span>Google </span>Map</h2>
                            <iframe src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d28016.98487005694!2d77.3668959!3d28.6260722!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x41b6d8c72c776ac4!2sJaipuria%20Mall!5e0!3m2!1sen!2sin!4v1579205992447!5m2!1sen!2sin" width="500" height="280" frameborder="0" style={{border:'0'}} allowfullscreen=""></iframe>

                        </div>

                    </div>

                    <div className="col-md-6">
                        <div className="headline">
                            <h2 className="headTitle"><span>Meet </span>Here</h2>
                            <ul className="contact-widge">
                                <li><h4> <b>BK Arogyam</b> </h4></li>
                                <li><span>Address : </span>G-8 Area, Rajouri Garden,New Delhi-110064</li>
                                <li><span>Office Tel. : </span>011-25120002 ,011-25120003 ,011-25120004</li>
                                <li><span>Fax. : </span>011-26155305</li>
                                <li><span>Email : </span>Info@Gtbpi.in</li>
                            </ul>
                        </div>
                    </div>



                </div>


                <style jsx>{`
                    .headline{
                        display: block;
                        margin: 7px 0 25px 0;
                        border-bottom: 1px dotted #e4e9f0;
                        padding-top: 20px;
                    }
                    .headTitle {
                        padding-bottom: 5px;
                        display: inline-block;
                        border-bottom: 2px solid green;
                        text-transform: capitalize;
                        font-family: auto;
                    }
                    .contact-widge {
                        list-style: none ;
                        font-size: 15px;
                        font-weight: 300;
                    }
                    .contact-widge>li>span{
                        font-weight: 600;
                    }

                    `}

                </style>
            </Layout>
        );
    }
}

Contact.getInitialProps = async function() {
    const res = await fetch(CONTACT_US);
    const data = await res.json();

    return {
        contact: data.results
    };
};



export default Contact;
