import Header from './Header';
import Footer from './Footer';
import { DISEASE_LIST_API } from '../constant/apiUrl';

class Layout extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            diseases:[],
        }
    }

    componentDidMount(){

    }


    loadDisease =async()=>{

        await fetch(DISEASE_LIST_API)
        .then(response => response.json())
        .then((data) =>
            this.setState({
                diseases: data.results,
            })
        )
    }


    render() {

        return (
            <section>
                <Header/>
                    {this.props.children}
                <Footer/>
            </section>
        );
    }
}

export default Layout;
